import express from 'express';
import { WebSocketServer } from 'ws';
import { createServer } from 'http';
import path from 'path';
import { fileURLToPath } from 'url';
import dotenv from 'dotenv';
import { CursorAgent } from './core/cursor-agent.js';
import { ProjectScanner } from './core/project-scanner.js';
import { Memory } from './core/memory.js';
import { Trust } from './core/trust.js';
import { CostTracker } from './core/cost-tracker.js';
import { loadConfig } from './utils/config.js';
import { logger } from './utils/logger.js';

// Load environment variables
dotenv.config();

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

class JeevesServer {
  private app: express.Application;
  private server: any;
  private wss: WebSocketServer;
  private agent: CursorAgent;
  private projectScanner: ProjectScanner;
  private memory: Memory;
  private trust: Trust;
  private costTracker: CostTracker;
  private config: any;

  constructor() {
    this.app = express();
    this.server = createServer(this.app);
    this.wss = new WebSocketServer({ server: this.server });

    this.init();
  }

  private async init() {
    try {
      // Load configuration
      this.config = await loadConfig();

      // Initialize core systems
      this.memory = new Memory(this.config.memory);
      this.trust = new Trust();
      this.costTracker = new CostTracker();
      this.projectScanner = new ProjectScanner(this.config.projects);
      this.agent = new CursorAgent(this.config, this.memory, this.trust, this.costTracker);

      // Setup middleware
      this.setupMiddleware();

      // Setup routes
      this.setupRoutes();

      // Setup WebSocket
      this.setupWebSocket();

      // Start scanning projects
      await this.projectScanner.scanAll();

      logger.info('Jeeves initialized successfully');
    } catch (error) {
      logger.error('Failed to initialize Jeeves:', error);
      process.exit(1);
    }
  }

  private setupMiddleware() {
    this.app.use(express.json({ limit: '10mb' }));
    this.app.use(express.static(path.join(__dirname, '../web')));
  }

  private setupRoutes() {
    // API routes
    this.app.post('/api/command', async (req, res) => {
      try {
        const { content, attachments = [] } = req.body;
        const result = await this.agent.processMessage(content, { attachments });
        res.json({ success: true, result });
      } catch (error) {
        logger.error('Command processing error:', error);
        res.status(500).json({ success: false, error: error.message });
      }
    });

    this.app.get('/api/status', async (req, res) => {
      try {
        const status = await this.getSystemStatus();
        res.json(status);
      } catch (error) {
        logger.error('Status error:', error);
        res.status(500).json({ error: error.message });
      }
    });

    this.app.get('/api/projects', async (req, res) => {
      try {
        const projects = await this.projectScanner.getProjects();
        res.json(projects);
      } catch (error) {
        logger.error('Projects error:', error);
        res.status(500).json({ error: error.message });
      }
    });

    this.app.get('/api/conversations/download', async (req, res) => {
      try {
        const format = req.query.format as string || 'json';
        const data = await this.memory.exportConversations(format);

        const filename = `jeeves-conversations-${new Date().toISOString().split('T')[0]}.${format}`;
        res.setHeader('Content-Disposition', `attachment; filename="${filename}"`);
        res.setHeader('Content-Type', format === 'json' ? 'application/json' : 'text/markdown');
        res.send(data);
      } catch (error) {
        logger.error('Export error:', error);
        res.status(500).json({ error: error.message });
      }
    });

    // Serve web UI
    this.app.get('/', (req, res) => {
      res.sendFile(path.join(__dirname, '../web/index.html'));
    });
  }

  private setupWebSocket() {
    this.wss.on('connection', (ws) => {
      logger.info('WebSocket client connected');

      // Send initial status
      this.sendToClient(ws, 'status', this.getSystemStatus());
      this.sendToClient(ws, 'projects', this.projectScanner.getProjects());

      ws.on('close', () => {
        logger.info('WebSocket client disconnected');
      });

      ws.on('error', (error) => {
        logger.error('WebSocket error:', error);
      });
    });

    // Setup agent event listeners
    this.agent.on('response', (data) => {
      this.broadcastToClients('response', data);
    });

    this.agent.on('stream_start', (data) => {
      this.broadcastToClients('stream_start', data);
    });

    this.agent.on('stream_chunk', (data) => {
      this.broadcastToClients('stream_chunk', data);
    });

    this.agent.on('stream_end', (data) => {
      this.broadcastToClients('stream_end', data);
    });

    this.agent.on('pending_changes', (data) => {
      this.broadcastToClients('pending_changes', data);
    });

    this.agent.on('log', (data) => {
      this.broadcastToClients('log', data);
    });
  }

  private sendToClient(ws: any, type: string, payload: any) {
    if (ws.readyState === ws.OPEN) {
      ws.send(JSON.stringify({ type, payload }));
    }
  }

