import React, { useRef } from 'react';
import { Expense } from '../types/expense';
import { exportImportService } from '../services/exportImportService';

interface ExportImportProps {
  expenses: Expense[];
  onImportExpenses: (expenses: Expense[]) => void;
}

const ExportImport: React.FC<ExportImportProps> = ({ expenses, onImportExpenses }) => {
  const fileInputRef = useRef<HTMLInputElement>(null);

  const handleExport = () => {
    if (expenses.length === 0) {
      alert('No expenses to export');
      return;
    }
    exportImportService.exportExpenses(expenses);
  };

  const handleImportClick = () => {
    fileInputRef.current?.click();
  };

  const handleFileChange = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    try {
      const importedExpenses = await exportImportService.importExpenses(file);

      if (importedExpenses.length === 0) {
        alert('No valid expenses found in the file');
        return;
      }

      const confirmImport = window.confirm(
        `Import ${importedExpenses.length} expenses? This will add to your existing expenses.`
      );

      if (confirmImport) {
        onImportExpenses(importedExpenses);
        alert(`Successfully imported ${importedExpenses.length} expenses!`);
      }
    } catch (error) {
      alert('Error importing file: ' + (error as Error).message);
    }

    // Reset file input
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  const exportToCSV = () => {
    if (expenses.length === 0) {
      alert('No expenses to export');
      return;
    }

    const csvHeaders = 'Date,Description,Category,Amount\n';
    const csvData = expenses.map(expense => 
      `${expense.date},"${expense.description}",${expense.category},${expense.amount}`
    ).join('\n');

    const csvContent = csvHeaders + csvData;
    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);

    const link = document.createElement('a');
    link.href = url;
    link.download = `expenses_${new Date().toISOString().split('T')[0]}.csv`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
    <div className="export-import card">
      <h3>Export & Import</h3>
      <div className="export-import-actions">
        <div className="export-section">
          <h4>Export Data</h4>
          <div className="button-group">
            <button 
              className="btn btn-primary"
              onClick={handleExport}
              disabled={expenses.length === 0}
            >
              Export as JSON
            </button>
            <button 
              className="btn btn-secondary"
              onClick={exportToCSV}
              disabled={expenses.length === 0}
            >
              Export as CSV
            </button>
          </div>
          <small className="help-text">
            Export your expenses to backup or use in other applications
          </small>
        </div>

        <div className="import-section">
          <h4>Import Data</h4>
          <button 
            className="btn btn-outline"
            onClick={handleImportClick}
          >
            Import JSON File
          </button>
          <input
            ref={fileInputRef}
            type="file"
            accept=".json"
            onChange={handleFileChange}
            style={{ display: 'none' }}
          />
          <small className="help-text">
            Import expenses from a previously exported JSON file
          </small>
        </div>
      </div>
    </div>
  );
};

export default ExportImport;
```

