import React, { useState, useEffect } from 'react';
import { Expense } from './types/expense';
import { storageService } from './services/localStorage';
import ExpenseForm from './components/ExpenseForm';
import ExpenseList from './components/ExpenseList';
import ExpenseSummary from './components/ExpenseSummary';
import BudgetTracker from './components/BudgetTracker';
import ExpenseCharts from './components/ExpenseCharts';
import FilterControls from './components/FilterControls';
import ExportImport from './components/ExportImport';

interface FilterState {
  category: string;
  dateFrom: string;
  dateTo: string;
  minAmount: string;
  maxAmount: string;
}

function App() {
  const [expenses, setExpenses] = useState<Expense[]>([]);
  const [filteredExpenses, setFilteredExpenses] = useState<Expense[]>([]);
  const [activeTab, setActiveTab] = useState<'overview' | 'charts' | 'budget'>('overview');

  useEffect(() => {
    const savedExpenses = storageService.getExpenses();
    setExpenses(savedExpenses);
    setFilteredExpenses(savedExpenses);
  }, []);

  const addExpense = (expense: Omit<Expense, 'id'>) => {
    const newExpense: Expense = {
      ...expense,
      id: Date.now().toString(),
    };
    const updatedExpenses = [...expenses, newExpense];
    setExpenses(updatedExpenses);
    storageService.saveExpenses(updatedExpenses);
    applyFilters(updatedExpenses, getCurrentFilters());
  };

  const deleteExpense = (id: string) => {
    const updatedExpenses = expenses.filter(expense => expense.id !== id);
    setExpenses(updatedExpenses);
    storageService.saveExpenses(updatedExpenses);
    applyFilters(updatedExpenses, getCurrentFilters());
  };

  const getCurrentFilters = (): FilterState => {
    return {
      category: 'all',
      dateFrom: '',
      dateTo: '',
      minAmount: '',
      maxAmount: ''
    };
  };

  const applyFilters = (expenseList: Expense[], filters: FilterState) => {
    let filtered = [...expenseList];

    // Filter by category
    if (filters.category && filters.category !== 'all') {
      filtered = filtered.filter(expense => expense.category === filters.category);
    }

    // Filter by date range
    if (filters.dateFrom) {
      filtered = filtered.filter(expense => expense.date >= filters.dateFrom);
    }
    if (filters.dateTo) {
      filtered = filtered.filter(expense => expense.date <= filters.dateTo);
    }

    // Filter by amount range
    if (filters.minAmount) {
      const minAmount = parseFloat(filters.minAmount);
      filtered = filtered.filter(expense => expense.amount >= minAmount);
    }
    if (filters.maxAmount) {
      const maxAmount = parseFloat(filters.maxAmount);
      filtered = filtered.filter(expense => expense.amount <= maxAmount);
    }

    setFilteredExpenses(filtered);
  };

  const handleFilterChange = (filters: FilterState) => {
    applyFilters(expenses, filters);
  };

  const handleImportExpenses = (importedExpenses: Expense[]) => {
    // Generate new IDs for imported expenses to avoid conflicts
    const expensesWithNewIds = importedExpenses.map(expense => ({
      ...expense,
      id: `${Date.now()}-${Math.random().toString(36).substr(2, 9)}`
    }));

    const updatedExpenses = [...expenses, ...expensesWithNewIds];
    setExpenses(updatedExpenses);
    storageService.saveExpenses(updatedExpenses);
    applyFilters(updatedExpenses, getCurrentFilters());
  };

  return (
    <div className="container">
      <header>
        <h1>ðŸ’° Expense Tracker</h1>
        <p>Track your spending, manage your budget, and achieve your financial goals</p>
      </header>

      <nav className="tab-navigation">
        <button 
          className={`tab-button ${activeTab === 'overview' ? 'active' : ''}`}
          onClick={() => setActiveTab('overview')}
        >
          Overview
        </button>
        <button 
          className={`tab-button ${activeTab === 'charts' ? 'active' : ''}`}
          onClick={() => setActiveTab('charts')}
        >
          Charts
        </button>
        <button 
          className={`tab-button ${activeTab === 'budget' ? 'active' : ''}`}
          onClick={() => setActiveTab('budget')}
        >
          Budget
        </button>
      </nav>

      <main>
        {activeTab === 'overview' && (
          <>
            <div className="card">
              <ExpenseSummary expenses={filteredExpenses} />
            </div>

            <div className="card">
              <h2>Add New Expense</h2>
              <ExpenseForm onAddExpense={addExpense} />
            </div>

            <FilterControls onFilterChange={handleFilterChange} />

            <div className="card">
              <h2>Expenses ({filteredExpenses.length})</h2>
              <ExpenseList expenses={filteredExpenses} onDeleteExpense={deleteExpense} />
            </div>

            <ExportImport 
              expenses={expenses} 
              onImportExpenses={handleImportExpenses} 
            />
          </>
        )}

        {activeTab === 'charts' && (
          <>
            <div className="card">
              <ExpenseSummary expenses={filteredExpenses} />
            </div>

            <FilterControls onFilterChange={handleFilterChange} />

            {filteredExpenses.length > 0 ? (
              <ExpenseCharts expenses={filteredExpenses} />
            ) : (
              <div className="card">
                <div className="empty-state">
                  <h3>No data to display</h3>
                  <p>Add some expenses or adjust your filters to see charts.</p>
                </div>
              </div>
            )}
          </>
        )}

        {activeTab === 'budget' && (
          <>
            <BudgetTracker expenses={filteredExpenses} />

            <FilterControls onFilterChange={handleFilterChange} />

            <div className="card">
              <h2>Budget vs Actual Spending</h2>
              <ExpenseSummary expenses={filteredExpenses} />
            </div>
          </>
        )}
      </main>
    </div>
  );
}

export default App;
```

