  margin-bottom: 1.5rem;
}

.form-group label {
  display: block;
  margin-bottom: 0.5rem;
  font-weight: 500;
  color: #374151;
}

.form-group input,
.form-group select,
.form-group textarea {
  width: 100%;
  padding: 0.75rem;
  border: 2px solid #e5e7eb;
  border-radius: 8px;
  font-size: 1rem;
  transition: border-color 0.2s ease;
}

.form-group input:focus,
.form-group select:focus,
.form-group textarea:focus {
  outline: none;
  border-color: #4f46e5;
  box-shadow: 0 0 0 3px rgba(79, 70, 229, 0.1);
}

.form-row {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 1rem;
}

/* Filter Controls */
.filter-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 1rem;
  align-items: end;
}

.filter-actions {
  display: flex;
  align-items: end;
}

/* Export Import */
.export-import-actions {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 2rem;
}

.export-section,
.import-section {
  text-align: center;
}

.export-section h4,
.import-section h4 {
  margin-bottom: 1rem;
  color: #374151;
}

.button-group {
  display: flex;
  gap: 0.5rem;
  justify-content: center;
  margin-bottom: 0.5rem;
}

.help-text {
  color: #6b7280;
  font-size: 0.8rem;
  display: block;
  margin-top: 0.5rem;
}

/* Expense List */
.expense-list {
  display: flex;
  flex-direction: column;
  gap: 1rem;
}

.expense-item {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1rem;
  background: #f8fafc;
  border-radius: 8px;
  border: 1px solid #e2e8f0;
  transition: all 0.2s ease;
}

.expense-item:hover {
  background: #f1f5f9;
  border-color: #cbd5e1;
}

.expense-info {
  flex: 1;
}

.expense-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 0.5rem;
}

.expense-description {
  font-weight: 600;
  color: #1f2937;
  margin: 0;
}

.expense-amount {
  font-weight: 700;
  color: #4f46e5;
  font-size: 1.1rem;
}

.expense-meta {
  display: flex;
  gap: 1rem;
  font-size: 0.9rem;
  color: #6b7280;
}

.expense-category {
  background: #e0e7ff;
  color: #3730a3;
  padding: 0.25rem 0.5rem;
  border-radius: 4px;
  font-size: 0.8rem;
  font-weight: 500;
}

/* Expense Summary */
.expense-summary h2 {
  color: #1f2937;
  margin-bottom: 1.5rem;
}

.summary-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 1rem;
  margin-bottom: 2rem;
}

.summary-card {
  background: linear-gradient(135deg, #f8fafc 0%, #e2e8f0 100%);
  padding: 1.5rem;
  border-radius: 12px;
  text-align: center;
  border: 1px solid #e2e8f0;
}

.summary-card h3 {
  color: #64748b;
  font-size: 0.9rem;
  font-weight: 500;
  text-transform: uppercase;
  letter-spacing: 0.05em;
  margin-bottom: 0.5rem;
}

.summary-amount {
  font-size: 1.8rem;
  font-weight: 700;
  color: #1e293b;
  margin-bottom: 0.25rem;
}

.summary-amount.total {
  color: #4f46e5;
  font-size: 2.2rem;
}

.summary-card small {
  color: #64748b;
  font-size: 0.8rem;
}

/* Category Breakdown */
.category-breakdown {
  margin-top: 2rem;
}

.category-breakdown h3 {
  color: #1f2937;
  margin-bottom: 1rem;
  font-size: 1.2rem;
}

.category-list {
  display: flex;
  flex-direction: column;
  gap: 1rem;
}

.category-item {
  background: #f8fafc;
  padding: 1rem;
  border-radius: 8px;
  border: 1px solid #e2e8f0;
}

.category-info {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 0.5rem;
}

.category-name {
  font-weight: 500;
  color: #374151;
}

.category-amount {
  font-weight: 600;
  color: #1f2937;
}

.category-bar {
  height: 8px;
  background-color: #e5e7eb;
  border-radius: 4px;
  overflow: hidden;
  margin-bottom: 0.25rem;
}

.category-fill {
  height: 100%;
  background: linear-gradient(90deg, #4f46e5, #7c3aed);
  border-radius: 4px;
  transition: width 0.3s ease;
}

.category-percentage {
  font-size: 0.8rem;
  color: #6b7280;
}

/* Budget Tracker */
.budget-categories {
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
}

.budget-category {
  background: #f8fafc;
  padding: 1.5rem;
  border-radius: 12px;
  border: 1px solid #e2e8f0;
}

.budget-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 1rem;
}

.budget-amounts {
  font-weight: 600;
  color: #1f2937;
}

.budget-bar {
  height: 12px;
  background-color: #e5e7eb;
  border-radius: 6px;
  overflow: hidden;
  margin-bottom: 0.5rem;
}

.budget-fill {
  height: 100%;
  border-radius: 6px;
  transition: width 0.3s ease;
}

.budget-good .budget-fill {
  background: linear-gradient(90deg, #10b981, #059669);
}

.budget-warning .budget-fill {
  background: linear-gradient(90deg, #f59e0b, #d97706);
}

.budget-danger .budget-fill {
  background: linear-gradient(90deg, #ef4444, #dc2626);
}

.budget-percentage {
  font-size: 0.9rem;
  color: #6b7280;
  font-weight: 500;
}

/* Charts */
.expense-charts {
  background: white;
  border-radius: 12px;
  padding: 2rem;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.05);
  margin-bottom: 1.5rem;
}

.charts-container {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 2rem;
}

.chart-wrapper {
  background: #f8fafc;
  padding: 1.5rem;
  border-radius: 8px;
  border: 1px solid #e2e8f0;
}

/* Empty State */
.empty-state {
  text-align: center;
  padding: 3rem 1rem;
  color: #6b7280;
}

.empty-state h3 {
  color: #374151;
  margin-bottom: 0.5rem;
}

.empty-state p {
  font-size: 0.9rem;
}

/* Responsive Design */
@media (max-width: 768px) {
  .container {
    padding: 0.5rem;
  }

  .card {
    padding: 1rem;
  }

  header h1 {
    font-size: 2rem;
  }

  .tab-navigation {
    flex-direction: column;
  }

  .filter-grid {
    grid-template-columns: 1fr;
  }

  .export-import-actions {
    grid-template-columns: 1fr;
    gap: 1rem;
  }

  .button-group {
    flex-direction: column;
  }

  .charts-container {
    grid-template-columns: 1fr;
  }

  .expense-header {
    flex-direction: column;
    align-items: flex-start;
    gap: 0.5rem;
  }

  .expense-meta {
    flex-direction: column;
    gap: 0.5rem;
  }

  .summary-grid {
    grid-template-columns: 1fr;
  }

  .form-row {
    grid-template-columns: 1fr;
  }
}

@media (max-width: 480px) {
  header {
    padding: 1.5rem 0;
  }

  header h1 {
    font-size: 1.8rem;
  }

  .summary-amount {
    font-size: 1.5rem;
  }

  .summary-amount.total {
    font-size: 1.8rem;
  }
}
```

