import React, { useState } from 'react';
import { Expense } from '../types';
import ExpenseItem from './ExpenseItem';

interface ExpenseListProps {
  expenses: Expense[];
  onDeleteExpense: (id: string) => void;
  onEditExpense: (id: string, expense: Omit<Expense, 'id'>) => void;
}

const ExpenseList: React.FC<ExpenseListProps> = ({ expenses, onDeleteExpense, onEditExpense }) => {
  const [editingId, setEditingId] = useState<string | null>(null);

  if (expenses.length === 0) {
    return (
      <div className="bg-white p-8 rounded-lg shadow-md text-center">
        <p className="text-gray-500">No expenses recorded yet. Add your first expense!</p>
      </div>
    );
  }

  return (
    <div className="bg-white rounded-lg shadow-md overflow-hidden">
      <div className="px-6 py-4 border-b border-gray-200">
        <h2 className="text-xl font-semibold">Expense History</h2>
      </div>
      <div className="divide-y divide-gray-200">
        {expenses.map(expense => (
          <ExpenseItem
            key={expense.id}
            expense={expense}
            isEditing={editingId === expense.id}
            onEdit={() => setEditingId(expense.id)}
            onCancelEdit={() => setEditingId(null)}
            onSaveEdit={(updatedExpense) => {
              onEditExpense(expense.id, updatedExpense);
              setEditingId(null);
            }}
            onDelete={() => onDeleteExpense(expense.id)}
          />
        ))}
      </div>
    </div>
  );
};

export default ExpenseList;
