import React from 'react';
import { Expense, Category, CategoryTotal } from '../types';

interface ExpenseSummaryProps {
  expenses: Expense[];
}

const ExpenseSummary: React.FC<ExpenseSummaryProps> = ({ expenses }) => {
  const totalAmount = expenses.reduce((sum, expense) => sum + expense.amount, 0);

  const categoryTotals: CategoryTotal[] = expenses.reduce((acc, expense) => {
    const existing = acc.find(item => item.category === expense.category);
    if (existing) {
      existing.total += expense.amount;
    } else {
      acc.push({
        category: expense.category,
        total: expense.amount,
        percentage: 0,
      });
    }
    return acc;
  }, [] as CategoryTotal[]);

  categoryTotals.forEach(cat => {
    cat.percentage = totalAmount > 0 ? (cat.total / totalAmount) * 100 : 0;
  });

  categoryTotals.sort((a, b) => b.total - a.total);

  const currentMonth = new Date().toLocaleString('default', { month: 'long', year: 'numeric' });
  const monthlyTotal = expenses
    .filter(expense => {
      const expenseDate = new Date(expense.date);
      const now = new Date();
      return expenseDate.getMonth() === now.getMonth() && 
             expenseDate.getFullYear() === now.getFullYear();
    })
    .reduce((sum, expense) => sum + expense.amount, 0);

  return (
    <div className="bg-white p-6 rounded-lg shadow-md">
      <h2 className="text-xl font-semibold mb-4">Summary</h2>

      <div className="space-y-4">
        <div>
          <p className="text-sm text-gray-600">Total Expenses</p>
          <p className="text-2xl font-bold text-gray-900">${totalAmount.toFixed(2)}</p>
        </div>

        <div>
          <p className="text-sm text-gray-600">{currentMonth} Total</p>
          <p className="text-xl font-semibold text-gray-900">${monthlyTotal.toFixed(2)}</p>
        </div>

        {categoryTotals.length > 0 && (
          <div>
            <p className="text-sm text-gray-600 mb-2">By Category</p>
            <div className="space-y-2">
              {categoryTotals.slice(0, 5).map(({ category, total, percentage }) => (
                <div key={category} className="flex justify-between items-center">
                  <span className="text-sm capitalize">{category}</span>
                  <div className="flex items-center gap-2">
                    <span className="text-sm font-medium">${total.toFixed(2)}</span>
                    <span className="text-xs text-gray-500">({percentage.toFixed(1)}%)</span>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ExpenseSummary;
