```newfile:src/App.tsx
import React, { useState, useEffect, FormEvent } from 'react';
import { Transaction, Summary } from './types';
import { loadTransactions, saveTransactions, generateId } from './storage';

const App: React.FC = () => {
  const [transactions, setTransactions] = useState<Transaction[]>([]);
  const [text, setText] = useState('');
  const [amount, setAmount] = useState('');

  useEffect(() => {
    setTransactions(loadTransactions());
  }, []);

  useEffect(() => {
    saveTransactions(transactions);
  }, [transactions]);

  const summary: Summary = transactions.reduce(
    (acc, t) => ({
      balance: acc.balance + t.amount,
      income: acc.income + (t.amount > 0 ? t.amount : 0),
      expense: acc.expense + (t.amount < 0 ? Math.abs(t.amount) : 0),
    }),
    { balance: 0, income: 0, expense: 0 }
  );

  const addTransaction = (e: FormEvent) => {
    e.preventDefault();
    if (!text.trim() || !amount.trim()) return;

    const newTransaction: Transaction = {
      id: generateId(),
      text: text.trim(),
      amount: parseFloat(amount),
      timestamp: Date.now(),
    };

    setTransactions(prev => [newTransaction, ...prev]);
    setText('');
    setAmount('');
  };

  const deleteTransaction = (id: number) => {
    setTransactions(prev => prev.filter(t => t.id !== id));
  };

  return (
    <div className="app">
      <div className="container">
        <header className="header">
          <h1 className="title">
            <span className="cyber-text">CYBER</span>
            <span className="expense-text">EXPENSE</span>
          </h1>
          <div className="balance-card">
            <div className="balance-label">TOTAL BALANCE</div>
            <div className="balance-amount">${summary.balance.toFixed(2)}</div>
          </div>
        </header>

        <div className="summary-grid">
          <div className="summary-card income">
            <div className="summary-label">INCOME</div>
            <div className="summary-amount">+${summary.income.toFixed(2)}</div>
          </div>
          <div className="summary-card expense">
            <div className="summary-label">EXPENSE</div>
            <div className="summary-amount">-${summary.expense.toFixed(2)}</div>
          </div>
        </div>

        <form className="transaction-form" onSubmit={addTransaction}>
          <h2 className="form-title">ADD TRANSACTION</h2>
          <div className="form-group">
            <input
              type="text"
              placeholder="Transaction description..."
              value={text}
              onChange={(e) => setText(e.target.value)}
              className="form-input"
              required
            />
          </div>
          <div className="form-group">
            <input
              type="number"
              step="0.01"
              placeholder="Amount (negative for expense)"
              value={amount}
              onChange={(e) => setAmount(e.target.value)}
              className="form-input"
              required
            />
          </div>
          <button type="submit" className="form-button">
            EXECUTE TRANSACTION
          </button>
        </form>

        <div className="transactions-section">
          <h2 className="section-title">TRANSACTION LOG</h2>
          <div className="transactions-list">
            {transactions.length === 0 ? (
              <div className="empty-state">NO TRANSACTIONS FOUND</div>
            ) : (
              transactions.map((transaction) => (
                <div
                  key={transaction.id}
                  className={`transaction-item ${transaction.amount >= 0 ? 'income' : 'expense'}`}
                >
                  <div className="transaction-content">
                    <div className="transaction-text">{transaction.text}</div>
                    <div className="transaction-amount">
                      {transaction.amount >= 0 ? '+' : ''}${transaction.amount.toFixed(2)}
                    </div>
                  </div>
                  <button
                    onClick={() => deleteTransaction(transaction.id)}
                    className="delete-button"
                  >
                    Ã—
                  </button>
                </div>
              ))
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
```

