import React from 'react';
import './ExpenseSummary.css';

const ExpenseSummary = ({ expenses }) => {
  const currentDate = new Date();
  const currentMonth = currentDate.getMonth();
  const currentYear = currentDate.getFullYear();

  // Calculate totals
  const totalExpenses = expenses.reduce((sum, expense) => sum + expense.amount, 0);

  const thisMonthExpenses = expenses.filter(expense => {
    const expenseDate = new Date(expense.date);
    return expenseDate.getMonth() === currentMonth && expenseDate.getFullYear() === currentYear;
  }).reduce((sum, expense) => sum + expense.amount, 0);

  const thisYearExpenses = expenses.filter(expense => {
    const expenseDate = new Date(expense.date);
    return expenseDate.getFullYear() === currentYear;
  }).reduce((sum, expense) => sum + expense.amount, 0);

  // Calculate category breakdown
  const categoryTotals = expenses.reduce((acc, expense) => {
    acc[expense.category] = (acc[expense.category] || 0) + expense.amount;
    return acc;
  }, {});

  const topCategories = Object.entries(categoryTotals)
    .sort(([,a], [,b]) => b - a)
    .slice(0, 3);

  // Calculate average daily spending this month
  const daysInMonth = new Date(currentYear, currentMonth + 1, 0).getDate();
  const currentDay = currentDate.getDate();
  const avgDailySpending = thisMonthExpenses / currentDay;

  return (
    <div className="expense-summary">
      <div className="summary-cards">
        <div className="summary-card total">
          <div className="card-icon">ðŸ’°</div>
          <div className="card-content">
            <h3>Total Expenses</h3>
            <p className="amount">${totalExpenses.toFixed(2)}</p>
            <span className="subtitle">{expenses.length} transactions</span>
          </div>
        </div>

        <div className="summary-card month">
          <div className="card-icon">ðŸ“…</div>
          <div className="card-content">
            <h3>This Month</h3>
            <p className="amount">${thisMonthExpenses.toFixed(2)}</p>
            <span className="subtitle">Avg: ${avgDailySpending.toFixed(2)}/day</span>
          </div>
        </div>

        <div className="summary-card year">
          <div className="card-icon">ðŸ“Š</div>
          <div className="card-content">
            <h3>This Year</h3>
            <p className="amount">${thisYearExpenses.toFixed(2)}</p>
            <span className="subtitle">Monthly avg: ${(thisYearExpenses / (currentMonth + 1)).toFixed(2)}</span>
          </div>
        </div>
      </div>

      {topCategories.length > 0 && (
        <div className="top-categories">
          <h3>Top Spending Categories</h3>
          <div className="category-list">
            {topCategories.map(([category, amount], index) => (
              <div key={category} className="category-item">
                <span className="category-rank">#{index + 1}</span>
                <span className="category-name">{category}</span>
                <span className="category-amount">${amount.toFixed(2)}</span>
                <div className="category-bar">
                  <div 
                    className="category-fill" 
                    style={{ width: `${(amount / topCategories[0][1]) * 100}%` }}
                  ></div>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

