    { value: 'Other', icon: 'ðŸ“¦', color: '#607d8b' }
  ];

  const getCategoryInfo = (categoryName) => {
    return categories.find(cat => cat.value === categoryName) || categories[categories.length - 1];
  };

  const handleEdit = () => {
    if (!editForm.description.trim() || !editForm.amount || parseFloat(editForm.amount) <= 0) return;
    onEdit(expense.id, {
