import React, { useState, useEffect } from 'react';
import './BudgetTracker.css';

const BudgetTracker = ({ expenses }) => {
  const [budgets, setBudgets] = useState({});
  const [showBudgetForm, setShowBudgetForm] = useState(false);
  const [newBudget, setNewBudget] = useState({ category: 'Food', amount: '' });

  const categories = ['Food', 'Transportation', 'Entertainment', 'Utilities', 'Healthcare', 'Shopping', 'Other'];

  useEffect(() => {
    const savedBudgets = localStorage.getItem('budgets');
    if (savedBudgets) {
      setBudgets(JSON.parse(savedBudgets));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('budgets', JSON.stringify(budgets));
  }, [budgets]);

  const currentDate = new Date();
  const currentMonth = currentDate.getMonth();
  const currentYear = currentDate.getFullYear();

  const monthlyExpenses = expenses.filter(expense => {
    const expenseDate = new Date(expense.date);
    return expenseDate.getMonth() === currentMonth && expenseDate.getFullYear() === currentYear;
  });

  const categorySpending = monthlyExpenses.reduce((acc, expense) => {
    acc[expense.category] = (acc[expense.category] || 0) + expense.amount;
    return acc;
  }, {});

  const handleAddBudget = (e) => {
    e.preventDefault();
    if (!newBudget.amount || parseFloat(newBudget.amount) <= 0) return;

    setBudgets({
      ...budgets,
      [newBudget.category]: parseFloat(newBudget.amount)
    });

    setNewBudget({ category: 'Food', amount: '' });
    setShowBudgetForm(false);
  };

  const removeBudget = (category) => {
    const updatedBudgets = { ...budgets };
    delete updatedBudgets[category];
    setBudgets(updatedBudgets);
  };

  const getProgressColor = (percentage) => {
    if (percentage <= 50) return '#4caf50';
    if (percentage <= 75) return '#ff9800';
    if (percentage <= 100) return '#f44336';
    return '#d32f2f';
  };

  return (
    <div className="budget-tracker">
      <div className="budget-header">
        <h3>ðŸ“Š Monthly Budget Tracker</h3>
        <button 
          onClick={() => setShowBudgetForm(!showBudgetForm)}
          className="add-budget-btn"
        >
          {showBudgetForm ? 'âŒ' : 'âž•'} Budget
        </button>
      </div>

      {showBudgetForm && (
        <form onSubmit={handleAddBudget} className="budget-form">
          <select
            value={newBudget.category}
            onChange={(e) => setNewBudget({ ...newBudget, category: e.target.value })}
          >
            {categories.map(cat => (
              <option key={cat} value={cat}>{cat}</option>
            ))}
          </select>
          <div className="budget-amount-input">
            <span className="currency">$</span>
            <input
              type="number"
              value={newBudget.amount}
              onChange={(e) => setNewBudget({ ...newBudget, amount: e.target.value })}
              placeholder="Budget amount"
              step="0.01"
              min="0.01"
              required
            />
          </div>
          <button type="submit">Set Budget</button>
        </form>
      )}

      <div className="budget-list">
        {Object.entries(budgets).map(([category, budget]) => {
          const spent = categorySpending[category] || 0;
          const percentage = (spent / budget) * 100;
          const remaining = budget - spent;

          return (
            <div key={category} className="budget-item">
              <div className="budget-info">
                <div className="budget-category">
                  <span className="category-name">{category}</span>
                  <button 
                    onClick={() => removeBudget(category)}
                    className="remove-budget"
                    title="Remove budget"
                  >
                    ðŸ—‘ï¸
                  </button>
                </div>
                <div className="budget-amounts">
                  <span className="spent">${spent.toFixed(2)} spent</span>
                  <span className="budget-total">of ${budget.toFixed(2)}</span>
                </div>
                <div className="remaining">
                  {remaining >= 0 ? (
                    <span className="remaining-positive">${remaining.toFixed(2)} remaining</span>
                  ) : (
                    <span className="remaining-negative">${Math.abs(remaining).toFixed(2)} over budget!</span>
                  )}
                </div>
              </div>
              <div className="budget-progress">
                <div className="progress-bar">
                  <div 
                    className="progress-fill"
                    style={{ 
                      width: `${Math.min(percentage, 100)}%`,
                      backgroundColor: getProgressColor(percentage)
                    }}
                  ></div>
                </div>
                <span className="progress-percentage">
                  {percentage.toFixed(0)}%
                </span>
              </div>
            </div>
          );
        })}
      </div>

      {Object.keys(budgets).length === 0 && (
        <div className="no-budgets">
          <p>No budgets set yet. Click "âž• Budget" to get started!</p>
        </div>
      )}
    </div>
  );
};

export default BudgetTracker;
